.app {
	height: 950px
	width: calc(var(--appsmith-ui-width) * 1px);
	justify-content: center;
	border-radius: var(--appsmith-theme-borderRadius);
	border: 1px solid black;
	padding: 15px 15px;
	box-sizing: border-box;
	font-family: system-ui;
	background: #fff;
}

.container {
	display: grid;
	grid-template-columns: repeat(6, 1fr);
	grid-template-rows: auto;
	grid-gap: 20px;
	align-items: start;
}

.header {
	display: grid;
	background-color: #d6f5d6; 
	padding: 6px;
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	grid-template-areas: 
		"info info info ";
	font-weight: bold;
}

.info {
	grid-area: info;
	border: 1px solid black;
	padding: 10px;
	background-color: #f2f2f2; 
	border-radius: 10px;
	text-align: center;

	.rptDate {
	color: blue;
	}
	h3 {
	color: blue;
	}
}


.type-container {
    display: grid;
    grid-template-columns: 1fr; 
    justify-content: space-between;
    background-color: #f2f8fd;
    padding: 2px;

}

.type {
	display: grid;
  margin: 2px;
	box-shadow: 0 4px 2px lightgrey;
	border-radius: 10px;
	transition: 0.3s;

}
.type-hdr {
	background-color: #d6f5d8;
	border-radius: 10px;
	border: 2px solid black;
	padding: 2px;
	color: blue;
	font-size: 20px;
	text-align: left;
}	

.type-tbl {
	width: 97%; 
	margin: 2px;
	font-size: 13px;
	border-radius: 9px;
	transition: 0.3s;
}

