class Helper {
  static initApp() {
    const title = document.getElementById("title");
    title.innerHTML = this.title();
    const rptDate = document.getElementById("rptDate");
    rptDate.innerHTML = appsmith.model.rptDate;
    const details = document.getElementById("details");
    details.innerHTML = this.details();
  }
  static title() {
    const rptDtl = appsmith.model.title;
    const html = `
            <h2>${rptDtl}</h2><br>
            <h3>Brands</h3><br>`;
    return html;
  }
  static details() {
    const rptDtl = JSON.parse(appsmith.model.details);
    let html = `<br><div class="table-area">
							<table>
								<tr class="type-tbl">
									<th class="type-hdr-1">Brand</th>
								</tr>`;
    rptDtl.forEach(dtl => {
      html += `
                <tr class="table-row">
										<td class="type-col-1">${dtl.name}</td>
                </tr>`;
    });
    html += `
				</table>
			</div>`;
    return html;
  }
}
appsmith.onReady(() => {
  appsmith.onModelChange(() => {
    Helper.initApp();
  });
});

