.app {
  width: 1000px;
  justify-content: center;
  border-radius: var(--appsmith-theme-borderRadius);
  padding: 10px 10px;
  border: 1px solid black;
  box-sizing: border-box;
  font-family: system-ui;
  background: white;
}

.container {
  display: grid;
  grid-template-columns: repeat(6, 1fr);
  grid-template-rows: auto;
  grid-gap: 20px;
  align-items: start;
}

.header {
  display: grid;
  padding: 6px;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-template-areas: "info info info ";
  font-weight: bold;
}

.info {
  grid-area: info;
  border: 1px solid black;
  padding: 10px;
  border-radius: 10px;
  text-align: center;
}
.info .rptDate {
  color: blue;
}
.info h3 {
  color: blue;
}
.info .info-tbl {
  display: flex;
  justify-content: center;
  align-items: center;
}

.type-container {
  display: grid;
  grid-template-columns: 3fr;
  justify-content: space-between;
  background-color: white;
  padding: 2px;
}

.type-hdr {
  background-color: #d6f5d8;
  border-radius: 10px;
  border: 2px solid black;
  padding: 2px;
  color: blue;
  font-size: 15px;
  text-align: left;
}

.type-tbl {
  width: 97%;
  margin: 2px;
  font-size: 13px;
  border-radius: 9px;
  transition: 0.3s;
}

.type-hdr-name {
  text-align: left;
  width: 250px;
  border: 2px solid black;
  padding: 4px;
}

.type-hdr-date {
  text-align: center;
  width: 90px;
  border: 2px solid black;
  padding: 7px;
}

.type-hdr-med {
  text-align: center;
  width: 120px;
  border: 2px solid black;
  padding: 7px;
}

.type-hdr-wide {
  text-align: center;
  width: 200px;
  border: 2px solid black;
  padding: 7px;
}

.type-hdr-nbr {
  text-align: right;
  width: 50px;
  border: 2px solid black;
  padding: 7px;
}

.type-col-name {
  background-color: #ffffe6;
  text-align: left;
  font-weight: bold;
  border: 1px solid blue;
  padding: 3px;
}

.type-col-date {
  text-align: center;
  border: 1px solid blue;
  padding: 3px;
}

.type-col-nbr {
  text-align: right;
  border: 1px solid blue;
  padding: 7px;
}

