class Helper {
  static initApp() {
    const title = document.getElementById("title");
    title.innerHTML = this.title();
    const rptDate = document.getElementById("rptDate");
    rptDate.innerHTML = appsmith.model.rptDate;
    const detail = document.getElementById("detail");
    detail.innerHTML = this.detail();
  }
  static title() {
    const rptDtl = appsmith.model.title;
    const btchDtl = JSON.parse(appsmith.model.head);
    const html = `
            <h2>${rptDtl}</h2><br>
           	<div class="info-tbl">
		<table >
			<tr class="table-row">
				<td class="type-col-name">Type</td>
				<td class="type-col-name">${btchDtl.ingr_type}</td>
			</tr>
			<tr class="table-row">
				<td class="type-col-name">Ingredient</td>
				<td class="type-col-name">${btchDtl.ingr_name}</td>
			</tr>
			<tr class="table-row">
				<td class="type-col-name">Code</td>
				<td class="type-col-name">${btchDtl.ingr_code}</td>
			</tr>
			<tr class="table-row">
				<td class="type-col-name">Last Batch</td>
				<td class="type-col-name">${btchDtl.lastBatch}</td>
			</tr>
		</table>
	</div><br>
`;
    return html;
  }
  static detail() {
    const rptDtl = JSON.parse(appsmith.model.detail);
    const rptHead = JSON.parse(appsmith.model.head);
    let html = `
			<div class="type-container">
				<div class="type-hdr">	
					<h4>Recipes with ${rptHead.ingr_name} (${rptHead.ingr_code})</h4>
				</div>`;
    html += `<div class="table-area">

				<div class="table-area">
				<table>
					<tr class="type-tbl">
						<th class="type-hdr-name">Product</th>
						<th class="type-hdr-date">Measure</th>
					</tr>`;
    rptDtl.forEach(dtl => {
      html += `
					<tr class="table-row">
						<td class="type-col-name">${dtl.prd_type_n_name}</td>
						<td class="type-col-date">${dtl.ingr_meas}</td>

					</tr>`;
    });
    html += `
				</table>
			</div>`;
    return html;
  }
}
appsmith.onReady(() => {
  appsmith.onModelChange(() => {
    Helper.initApp();
  });
});

