class Helper {
  static initApp() {
    const logo = document.getElementById("logo");
    logo.innerHTML = appsmith.model.logo;
    const title = document.getElementById("title");
    title.innerHTML = this.title();
    const rptDate = document.getElementById("rptDate");
    rptDate.innerHTML = appsmith.model.rptDate;
    const ingrType = document.getElementById("ingrType");
    ingrType.innerHTML = this.ingrType();
  }
  static title() {
    const rptDtl = appsmith.model.title;
    const html = `
            <h2>${rptDtl}</h2><br>
            <h3>Active Ingredients</h3><br>`;
    return html;
  }
  static ingrType() {
    const rptDtl = JSON.parse(appsmith.model.ingrType);
    let html = `
				<div class="type-container">`;
    html += `<br><div class="table-area">
					<h4 class="type-hdr">${rptDtl.name}</h4>
					<table>
						<tr class="type-tbl">
						<th class="type-hdr-2" >Code</th>
						<th class="type-hdr-1" >Ingredient</th>
						<th class="type-hdr-2" >gm/oz</th>
						<th class="type-hdr-3" >Description</th>
						<th class="type-hdr-2" >Batches</th>
						<th class="type-hdr-2" >Units</th>
						<th class="type-hdr-2" >Min Rate</th>
						<th class="type-hdr-2" >Max Rate</th>
						<th class="type-hdr-2" >Avg Rate</th>


					</tr>`;
    html += this.ingrList(rptDtl.id);
    html += `
				</table>
			</div>`;
    return html;
  }
  static ingrList(typeID) {
    const rptDtl = JSON.parse(appsmith.model.ingr);
    const filteredIngr = rptDtl.filter(ingr => ingr.ingr_type_id === typeID);
    let html = ``;
    filteredIngr.forEach(ingr => {
      html += `
                <tr class="table-row">
										<td class="type-col-2">${ingr.ingr_code}</td>
										<td class="type-col-1">${ingr.ingr_name}</td>
										<td class="type-col-2">${ingr.gms_per_oz}</td>
                    <td class="type-col-3">${ingr.ingr_desc}</td>
										<td class="type-col-2">${ingr.batches}</td>
										<td class="type-col-2">${ingr.units}</td>
										<td class="type-col-2">$${ingr.min_rate}</td>
										<td class="type-col-2">$${ingr.max_rate}</td>
										<td class="type-col-2">$${ingr.avg_rate}</td>

                </tr>`;
    });
    return html;
  }
}
appsmith.onReady(() => {
  appsmith.onModelChange(() => {
    Helper.initApp();
  });
});

