class Helper {
  static initApp() {
    const logo = document.getElementById("logo");
    logo.innerHTML = appsmith.model.logo;
    title.innerHTML = this.title();
    const rptDate = document.getElementById("rptDate");
    rptDate.innerHTML = appsmith.model.rptDate;
    rcpe.innerHTML = this.rcpe();
    tasks.innerHTML = this.tasks();
  }
  static title() {
    let rptDtl = JSON.parse(appsmith.model.title);
    let html = `
	<h2>Product Batch Worksheet</h2><br>
	<h3>Type: ${rptDtl.prd_type} - ${rptDtl.prd_name}</h3><br>
  <h4>Batch: ${rptDtl.prd_btch_nbr} 
			<br>Date: ${rptDtl.prd_btch_date}
			<br>Location: ${rptDtl.location}
			<br>Qty:  		${rptDtl.qty_meas}</h4>

 `;
    return html;
  }
  static rcpe() {
    const rptDtl = JSON.parse(appsmith.model.ingrMap);
    const btchSelect = JSON.parse(appsmith.model.title);
    let html = `
			<div class="type-container">
				<div class="type-hdr">	
					<h4>Ingredient Batches: ${btchSelect.prd_btch_nbr}</h4> 
			</div>
			<div class="table-area">
				<div class="table-area">
					<table>
						<tr class="type-tbl">
						<th class="type-hdr-2">Order</th>
						<th class="type-hdr-1">Ingredient</th>
						<th class="type-hdr-3">Ingr Batch(es)</th>
						<th class="type-hdr-3">Description</th>
					</tr>`;
    rptDtl.forEach(ingr => {
      html += `
					<tr class="table-row">
						<td class="type-col-2">${ingr.ingr_ordr}</td>
						<td class="type-col-1">${ingr.ingr_name}</td>
						<td class="type-col-3">${ingr.ingrMaps}</td>
						<td class="type-col-3">${ingr.prd_ingr_desc}</td>
					</tr>`;
    });
    html += `
				</table>
			</div>`;
    return html;
  }
  static tasks() {
    const rptDtl = JSON.parse(appsmith.model.tasks);
    const btchSelect = JSON.parse(appsmith.model.title);
    let html = `
			<div class="type-container">
				<div class="type-hdr">	
					<h4>Batch Tasks: ${btchSelect.prd_btch_nbr}</h4> 
			</div>
			<div class="table-area">
				<div class="table-area">
					<table>
						<tr class="type-tbl">
						<th class="type-hdr-2">Order</th>
						<th class="type-hdr-1">Task</th>
						<th class="type-hdr-2">Value</th>
						<th class="type-hdr-3">Comments</th>
						<th class="type-hdr-3">Worker(s)</th>

					</tr>`;
    rptDtl.forEach(task => {
      html += `
					<tr class="table-row">
						<td class="type-col-2">${task.ordr}</td>
						<td class="type-col-1">${task.task_name}</td>
						<td class="type-col-2">${task.measure_value}</td>
						<td class="type-col-3">${task.comments}</td>
						<td class="type-col-3">${task.taskWrkrs}</td>

					</tr>`;
    });
    html += `
				</table>
			</div>`;
    return html;
  }
}

//Start rendering the report when widget is ready
appsmith.onReady(() => {
  //rerender the report when the data changes
  appsmith.onModelChange(() => {
    Helper.initApp();
  });
});

