.app {
	height: calc(var(--appsmith-ui-height) * 1px);
	width: 1200 * 1px);
	justify-content: center;
	border-radius: var(--appsmith-theme-borderRadius);
	padding: 15px 15px;
	box-sizing: border-box;
	font-family: system-ui;
	background: #fff;
}

.container {
	display: grid;
	grid-template-columns: repeat(6, 1fr);
	grid-template-rows: auto;
	grid-gap: 20px;
	align-items: start;
}

.header {
	display: grid;
	background-color: white; 
	border: 1px solid black;
	border-radius: 10px;
	padding: 6px;
	display: grid;
	grid-template-columns: repeat(6, 1fr);
	grid-template-areas: 
		"info info info info info";
	font-weight: bold;
}

.info {
	grid-area: info;
	padding: 5px;
	background-color: white; 
	border-radius: 20px;
	text-align: center;

	.rptDate {
	color: blue;
	}
	h3 {
	color: blue;
	}
	.info-tbl {
    display: flex;
    justify-content: center; /* Horizontally center the content */
    align-items: center; /* Vertically center the content */
}
}

.type-container {
    display: grid;
    grid-template-columns: 1fr; 
    justify-content: space-between;
    background-color: white;
    padding: 2px;
}

.type {
	display: grid;
  margin: 2px;
	box-shadow: 0 4px 2px lightgrey;
	border-radius: 10px;
	transition: 0.3s;

}
.type-hdr {
	background-color: #d6f5d8;
	border-radius: 10px;
	border: 2px solid black;
	padding: 2px;
	color: blue;
	font-size: 18px;
	text-align: left;
}	

.type-tbl {
	width: 97%; 
	margin: 2px;
	font-size: 15px;
	border-radius: 9px;
	transition: 0.3s;
}

	.type-hdr-1 {
			text-align: left;
			width: 300px;
			border: 2px solid black;
			padding: 4px;
		}
	.type-hdr-2 {
			text-align: left;
			width: 30px;
			border: 2px solid black;
			padding: 7px;
		}
	.type-hdr-3 {
			text-align: left;
			width: 600px;
			border: 2px solid black; 
			padding: 7px;
		}
	.type-col-1 {
			background-color: #ffffe6;
			text-align: left;
			font-size: 14px;
			border: 1px solid blue;
			padding: 3px;
		}
	.type-col-2 {
			text-align: center;
			border: 1px solid blue;
			font-size: 14px;
			padding: 5px;
		}
	.type-col-3 {
			text-align: left;
			border: 1px solid blue;
			font-size: 14px;
			padding: 7px;
		}

