.app {
  height: calc(var(--appsmith-ui-height) * 1px);
  width: 700px;
  justify-content: center;
  border-radius: var(--appsmith-theme-borderRadius);
  padding: 10px 10px;
  box-sizing: border-box;
  font-family: TimesNewRoman;
  background: #fff;
}

.container {
  display: grid;
  grid-template-columns: repeat(6, 1fr);
  grid-template-rows: auto;
  grid-gap: 10px;
  align-items: start;
}

.header {
  display: grid;
  background-color: #d6f5d6;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-template-areas: "info info info";
  font-weight: bold;
}

.info {
  grid-area: info;
  border: 1px solid black;
  padding: 1px;
  background-color: #f2f2f2;
  border-radius: 10px;
  text-align: center;
}
.info .rptDate {
  color: blue;
}
.info h3 {
  color: blue;
}

.type-container {
  display: grid;
  grid-template-columns: 1fr;
  justify-content: space-between;
  background-color: #f2f8fd;
  padding: 2px;
}

.type {
  display: grid;
  margin: 2px;
  box-shadow: 0 4px 2px lightgrey;
  border-radius: 10px;
  transition: 0.3s;
}

.type-hdr {
  background-color: #d6f5d8;
  border-radius: 10px;
  border: 2px solid black;
  padding: 2px;
  color: blue;
  font-size: 20px;
  text-align: left;
}

.type-tbl {
  width: 97%;
  margin: 2px;
  font-size: 17px;
  border-radius: 9px;
  transition: 0.3s;
}

.type-hdr-nbr {
  text-align: left;
  width: 230px;
  border: 2px solid black;
  padding: 4px;
}

.type-hdr-name {
  text-align: left;
  width: 200px;
  border: 2px solid black;
  padding: 7px;
}

.type-hdr-wide {
  text-align: left;
  width: 500px;
  border: 2px solid black;
  padding: 7px;
}

.type-hdr-narrow {
  text-align: center;
  width: 300px;
  border: 2px solid black;
  padding: 7px;
}

.type-col-name {
  background-color: #ffffe6;
  text-align: left;
  border: 1px solid blue;
  padding: 3px;
  font-weight: bold;
}

.type-col-2 {
  text-align: left;
  border: 1px solid blue;
  padding: 5px;
}

.type-col-3 {
  text-align: left;
  border: 1px solid blue;
  padding: 7px;
}

