class Helper {
  static initApp() {
    const title = document.getElementById("title");
    title.innerHTML = this.title();
    const details = document.getElementById("details");
    details.innerHTML = this.detail();
  }
  static title() {
    const rptDtl = appsmith.model.title;
    const html = `
            <h3>${rptDtl}</h3>`;
    return html;
  }
  static detail() {
    const rptDtl = JSON.parse(appsmith.model.details);
    const parents = new Set(rptDtl.map(item => item.parent));
    let html = '<div class="type-container">';
    parents.forEach(parent => {
      html += `<h5 class="type-hdr">${parent}</h5>`;
      html += this.details(parent);
    });
    // Add any other necessary logic or return the HTML
    return html;
  }
  static details(parent) {
    const rptDtl = JSON.parse(appsmith.model.details);
    const filtered = rptDtl.filter(dtl => dtl.parent === parent);
    let html = `
				<div class="table-area">
					<table>
						<tr class="type-tbl">
						<th class="type-hdr-name" >Name</th>
						<th class="type-hdr-wide" >Guide</th>
					</tr>`;
    filtered.forEach(dtl => {
      html += `
                <tr class="table-row">
										<td class="type-col-name">${dtl.name}</td>
										<td class="type-col-2">${dtl.description}</td>
                </tr>`;
    });
    html += `
				</table>
			</div>`;
    return html;
  }
}
appsmith.onReady(() => {
  appsmith.onModelChange(() => {
    Helper.initApp();
  });
});

