class Helper {
  static initApp() {
    const title = document.getElementById("title");
    title.innerHTML = this.title();
    const rptDate = document.getElementById("rptDate");
    rptDate.innerHTML = appsmith.model.rptDate;
    const prodType = document.getElementById("prodType");
    prodType.innerHTML = this.prodType();
  }
  static title() {
    const rptDtl = appsmith.model.title;
    const prodType = JSON.parse(appsmith.model.prodType);
    const html = `
            <h2>${rptDtl}</h2><br>
            <h3>Product Type: ${prodType.name}</h3><br>`;
    return html;
  }
  static prodType() {
    const rptDtl = JSON.parse(appsmith.model.prodType);
    let html = `<div class="type-container">`;
    html += `<br><div class="table-area">
							<table>
								<tr class="type-tbl">
									<th class="type-hdr-2">Code</th>
									<th class="type-hdr-1">Product</th>
									<th class="type-hdr-3">Description</th>
									<th class="type-hdr-2">Units</th>
									<th class="type-hdr-2">Location</th>
									<th class="type-hdr-4">Best By</th>
								</tr>`;
    html += this.prodList(rptDtl.id);
    html += `
							</table>
							<table>
								<tr class="type-tbl">
									<th class="type-hdr-2">Ordr</th>
									<th class="type-hdr-1">Task</th>
									<th class="type-hdr-3">Description</th>
								</tr>`;
    html += this.taskList(rptDtl.id);
    html += `
							</table>
						</div>`;
    return html;
  }
  static prodList(typeID) {
    const rptDtl = JSON.parse(appsmith.model.prodList);
    const filteredProd = rptDtl.filter(prod => prod.product_type_id === typeID);
    let html = `<br>
				<h5 class="type-hdr">Products</h5>`;
    filteredProd.forEach(prod => {
      html += `
                <tr class="table-row">

										<td class="type-col-2">${prod.code}</td>
									  <td class="type-col-1">${prod.name}</td>
                    <td class="type-col-3">${prod.description}</td>
										<td class="type-col-2">${prod.unit}</td>
										<td class="type-col-2">${prod.location}</td>
										<td class="type-col-2">${prod.best_by_days} Days</td>
                </tr>`;
    });
    return html;
  }
  static taskList(typeID) {
    const rptDtl = JSON.parse(appsmith.model.task);
    const filteredTasks = rptDtl.filter(task => task.product_type_id === typeID);
    let html = `<br>
				<h5 class="type-hdr">Tasks</h5>`;
    filteredTasks.forEach(task => {
      html += `
                <tr class="table-row">
                    <td class="type-col-2">${task.ordr}</td>
										<td class="type-col-1">${task.name}</td>
                    <td class="type-col-3">${task.description}</td>
                </tr>`;
    });
    return html;
  }
}
appsmith.onReady(() => {
  appsmith.onModelChange(() => {
    Helper.initApp();
  });
});

