.app {
  height: 950px;
  width: 850px;
  justify-content: center;
  border-radius: var(--appsmith-theme-borderRadius);
  border: 1px solid black;
  padding: 15px 15px;
  font-family: TimesNewRoman;
  background: #fff;
}

.container {
  display: grid;
  grid-template-columns: repeat(6, 1fr);
  grid-template-rows: auto;
  grid-gap: 20px;
  align-items: start;
}

.header {
  display: grid;
  background-color: white;
  padding: 6px;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-template-areas: "info info info";
  font-weight: bold;
}

.info {
  grid-area: info;
  border: 1px solid black;
  padding: 10px;
  background-color: white;
  border-radius: 10px;
  text-align: center;
}
.info .rptDate {
  color: blue;
}
.info h3 {
  color: blue;
}

.type-container {
  display: grid;
  grid-template-columns: 1fr;
  justify-content: space-between;
  background-color: white;
  padding: 2px;
}

.type {
  display: grid;
  margin: 2px;
  box-shadow: 0 4px 2px lightgrey;
  border-radius: 10px;
  transition: 0.3s;
}

.type-hdr {
  background-color: #d6f5d8;
  border-radius: 10px;
  border: 2px solid black;
  padding: 2px;
  color: blue;
  font-size: 20px;
  text-align: left;
}

.type-head-desc {
  background-color: #ffffe6;
  text-align: center;
  width: 500px;
  border: 1px solid blue;
  padding: 3px;
}

.type-tbl {
  width: 94%;
  margin: 2px;
  font-size: 13px;
  border-radius: 9px;
  transition: 0.3s;
}

.type-hdr-1 {
  text-align: left;
  width: 230px;
  border: 2px solid black;
  padding: 4px;
}

.type-hdr-2 {
  text-align: center;
  width: 30px;
  border: 2px solid black;
  padding: 7px;
}

.type-hdr-3 {
  text-align: left;
  width: 600px;
  border: 2px solid black;
  padding: 7px;
}

.type-hdr-4 {
  text-align: left;
  width: 100px;
  border: 2px solid black;
  padding: 7px;
}

.type-col-1 {
  background-color: #ffffe6;
  text-align: left;
  border: 1px solid blue;
  padding: 3px;
}

.type-col-2 {
  text-align: center;
  border: 1px solid blue;
  padding: 2px;
}

.type-col-3 {
  text-align: left;
  border: 1px solid blue;
  padding: 7px;
}

