class Helper {
  static initApp() {
    const title = document.getElementById("title");
    title.innerHTML = this.title();
    const rptDate = document.getElementById("rptDate");
    rptDate.innerHTML = appsmith.model.rptDate;
    const recipe = document.getElementById("recipe");
    recipe.innerHTML = this.recipe();
  }
  static title() {
    const rptDtl = appsmith.model.title;
    const desc = appsmith.model.head;
    const prod = appsmith.model.prod;
    const html = `
    <h2>${rptDtl}</h2><br>
		<h3>${prod}</h3><br>
    <h4 class="type-head-desc">${desc}</h4>
	</div><br>`;
    return html;
  }
  static recipe() {
    const rptDtl = JSON.parse(appsmith.model.recipe);
    const prod = appsmith.model.prod;
    let html = `<div class="type-container">
				<br>
				<div class="table-area">
					<h4 class="type-hdr">${prod}</h4>
						<table>
								<tr class="type-tbl">
									<th class="type-hdr-2" >Order</th>
									<th class="type-hdr-1" >Ingredient</th>
									<th class="type-hdr-4" >Qty Measure</th>
									<th class="type-hdr-3" >Comments</th>
								</tr>`;
    rptDtl.forEach(type => {
      html += `
                <tr class="table-row">
								                    <td class="type-col-2">${type.ingr_ordr}</td>
                    <td class="type-col-1">${type.ingr_name}</td>
										<td class="type-col-3">${type.ingr_qty_meas}</td>
                    <td class="type-col-3">${type.comments}</td>
                </tr>`;
    });
    html += `
							</table>
					</div>`;
    return html;
  }
  static taskList(typeID) {
    const rptDtl = JSON.parse(appsmith.model.task);
    const filteredTasks = rptDtl.filter(task => task.product_type_id === typeID);
    let html = `<br>
				<h5 class="type-hdr">Tasks</h5>`;
    filteredTasks.forEach(task => {
      html += `
                <tr class="table-row">
                    <td class="type-col-2">${task.ordr}</td>
										<td class="type-col-1">${task.name}</td>
                    <td class="type-col-3">${task.description}</td>
                </tr>`;
    });
    return html;
  }
}
appsmith.onReady(() => {
  appsmith.onModelChange(() => {
    Helper.initApp();
  });
});

