.app {
  height: calc(var(--appsmith-ui-height) * 1px);
  width: calc(var(--appsmith-ui-width) * 1px);
  justify-content: center;
  border-radius: var(--appsmith-theme-borderRadius);
  padding: 15px 15px;
  box-sizing: border-box;
  font-family: system-ui;
  background: #fff;
}

.container {
  display: grid;
  grid-template-columns: repeat(6, 1fr);
  grid-template-rows: auto;
  grid-gap: 20px;
  align-items: start;
}

.header {
  display: grid;
  background-color: #d6f5d6;
  padding: 6px;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  grid-template-areas: "info info info info";
  font-weight: bold;
}

.info {
  grid-area: info;
  border: 1px solid black;
  padding: 10px;
  background-color: #f2f2f2;
  border-radius: 10px;
  text-align: center;
}
.info .rptDate {
  color: blue;
}
.info h3 {
  color: blue;
}

th {
  padding: 5px;
  text-align: center;
  font-size: 12pt;
  border: 1px solid black;
}

td {
  padding: 5px;
  text-align: center;
  font-size: 12pt;
  border: 1px solid black;
}

.type-container {
  display: grid;
  grid-template-columns: 1fr;
  justify-content: space-between;
  background-color: #f2f8fd;
  padding: 2px;
}

.type {
  display: grid;
  margin: 2px;
  box-shadow: 0 4px 2px lightgrey;
  border-radius: 10px;
  transition: 0.3s;
}

.type-hdr {
  background-color: #d6f5d8;
  border-radius: 10px;
  border: 2px solid black;
  padding: 2px;
  color: blue;
  font-size: 15px;
  text-align: left;
}

.type-tbl {
  width: 97%;
  margin: 2px;
  font-size: 13px;
  border-radius: 9px;
  transition: 0.3s;
}

.type-hdr-1 {
  text-align: left;
  width: 230px;
  border: 2px solid black;
  padding: 4px;
}

.type-hdr-2 {
  text-align: center;
  width: 110px;
  border: 2px solid black;
  padding: 7px;
}

.type-hdr-3 {
  text-align: center;
  width: 230px;
  border: 2px solid black;
  padding: 7px;
}

.type-hdr-currency {
  text-align: right;
  width: 80px;
  border: 2px solid black;
  padding: 7px;
}

.type-col-1 {
  background-color: #ffffe6;
  text-align: left;
  border: 1px solid blue;
  padding: 3px;
}

.type-col-2 {
  text-align: center;
  border: 1px solid blue;
  padding: 5px;
}

.type-col-3 {
  text-align: center;
  border: 1px solid blue;
  padding: 7px;
}

.type-col-currency {
  text-align: right;
  border: 1px solid blue;
  padding: 7px;
}

