class Helper {
  static initApp() {
    const title = document.getElementById("title");
    title.innerHTML = this.title();
    const rptDate = document.getElementById("rptDate");
    rptDate.innerHTML = appsmith.model.rptDate;
    const details = document.getElementById("details");
    details.innerHTML = this.details();
  }
  static title() {
    const rptDtl = appsmith.model.title;
    const shop = JSON.parse(appsmith.model.shopEvent);
    const html = `
            <h2>${rptDtl}</h2><br>
            <h3>Shopping</h3><br>
						<h4>${shop.shop_name}</h4>
<br>
`;
    return html;
  }
  static details() {
    const rptDtl = JSON.parse(appsmith.model.details);
    const total = JSON.parse(appsmith.model.shopEvent);
    let html = `
			<div class="type-container">
			<br><div class="table-area">
			<div class="table-area">
				<table>
					<tr class="type-tbl">
						<th class="type-hdr-1">Ingredient</th>
						<th class="type-hdr-2">Batch</th>
						<th class="type-hdr-2">Lot</th>
						<th class="type-hdr-3">Detail</th>
						<th class="type-hdr-currency">Amount</th>
					</tr>`;
    rptDtl.forEach(dtl => {
      html += `
					<tr class="table-row">
						<td class="type-col-1">${dtl.ingr_name}</td>
						<td class="type-col-2">${dtl.ingr_btch_nbr}</td>
						<td class="type-col-2">${dtl.ingr_lot_nbr}</td>
						<td class="type-col-3">${dtl.purch_dtl}</td>
						<td class="type-col-currency">${dtl.purch_amt}</td>
					</tr>`;
    });
    html += `
				<tr class="table-row">
						<td class="type-col-1"></td>
						<td class="type-col-2"></td>
						<td class="type-col-2"></td>
						<td class="type-col-3"><strong>Total</strong></td>
						<td class="type-col-currency"><strong>${total.total_amount}</strong></td>
					</tr>;
				</table>
			</div>`;
    return html;
  }
}
appsmith.onReady(() => {
  appsmith.onModelChange(() => {
    Helper.initApp();
  });
});

