class Helper {
  static initApp() {
    const title = document.getElementById("title");
    title.innerHTML = this.title();
    const rptDate = document.getElementById("rptDate");
    rptDate.innerHTML = appsmith.model.rptDate;
    const details = document.getElementById("details");
    details.innerHTML = this.details();
  }
  static title() {
    const rptDtl = appsmith.model.title;
    const html = `
            <h2>${rptDtl}</h2><br>
            <h3>Vendors</h3><br>`;
    return html;
  }
  static details() {
    const rptDtl = JSON.parse(appsmith.model.details);
    let html = `
				<div class="type-container">`;
    html += `<div class="table-area">
					<table>
						<tr class="type-tbl">
						<th class="type-hdr-1" >Vendor Name</th>
						<th class="type-hdr-2" >Contact</th>
						<th class="type-hdr-2" >Phone</th>
						<th class="type-hdr-3" >Email</th>
					</tr>`;
    rptDtl.forEach(dtl => {
      html += `
                <tr class="table-row">
										<td class="type-col-1">${dtl.name}</td>
										<td class="type-col-2">${dtl.contact_name}</td>
										<td class="type-col-2">${dtl.contact_phone}</td>
                    <td class="type-col-3">${dtl.contact_email}</td>
                </tr>`;
    });
    html += `
				</table>
			</div>`;
    return html;
  }
  static list(typeID) {
    const rptDtl = JSON.parse(appsmith.details);
    let html = ``;
    rptDtl.forEach(dtl => {
      html += `
                <tr class="table-row">
										<td class="type-col-2">${dtl.name}</td>
										<td class="type-col-1">${dtl.contact_name}</td>
										<td class="type-col-2">${dtl.contact_phone}</td>
                    <td class="type-col-3">${dtl.contact_email}</td>
                </tr>`;
    });
    return html;
  }
}
appsmith.onReady(() => {
  appsmith.onModelChange(() => {
    Helper.initApp();
  });
});

