.app {
  height: 1000px;
  width: 800px;
  justify-content: center;
  border-radius: var(--appsmith-theme-borderRadius);
  padding: 29px 25px;
  box-sizing: border-box;
  font-family: "Times New Roman", Times, serif;
  background: #fff;
}

@media print {
  .noPrint {
    display: none;
  }
}
.container {
  display: grid;
  grid-template-columns: repeat(6, 1fr);
  grid-template-rows: auto;
  grid-gap: 20px;
  align-items: start;
}

.header {
  background-color: #d6f5d6;
  padding: 10px;
  display: grid;
  grid-template-columns: repeat(6, 1fr);
  grid-template-areas: "wf-img info info info info";
  font-weight: bold;
}

.wf-img {
  grid-area: wf-img;
  width: 30px;
  height: 30px;
  border-radius: 8px;
  padding: 1;
}

.info {
  grid-area: info;
  border: 1px solid black;
  padding: 10px;
  background-color: #f2f2f2;
  border-radius: 10px;
  text-align: center;
}

.rptDate {
  color: blue;
}

.table-grid {
  display: grid;
  grid-area: table;
  grid-template-columns: fr 0.5fr 0.7fr 1.8fr;
  border: 1px solid black;
}
.table-grid .table-head {
  font-weight: bold;
  border-right: 1px solid black;
  background-color: #f2f2f2;
  border: 1px solid black;
}
.table-grid .table-head .hd-1 {
  text-align: center;
  border-right: 1px solid black;
  padding: 7px;
}
.table-grid .table-head .hd-2 {
  text-align: left;
  border-right: 1px solid black;
  padding: 7px;
}
.table-grid .table-head .hd-3 {
  text-align: center;
  border-right: 1px solid black;
  padding: 7px;
}
.table-grid .table-head .hd-4 {
  text-align: center;
  padding: 7px;
}
.table-grid .table-row {
  border: 1px solid black;
}
.table-grid .table-row .col-1 {
  text-align: left;
  border-right: 1px solid black;
  padding: 7px;
}
.table-grid .table-row .col-2 {
  text-align: center;
  border-right: 1px solid black;
  padding: 7px;
}
.table-grid .table-row .col-3 {
  text-align: center;
  border-right: 1px solid black;
  padding: 7px;
}
.table-grid .table-row .col-4 {
  text-align: center;
  padding: 7px;
}

