class Helper {
  static initApp() {
    const title = document.getElementById("title");
    title.innerHTML = appsmith.model.title;
    const logo = document.getElementById("logo");
    logo.innerHTML = appsmith.model.logo;
    const rptDate = document.getElementById("rptDate");
    rptDate.innerHTML = appsmith.model.rptDate;
    rptDetail.innerHTML = this.rptHTML();
  }
  static rptHTML() {
    let rptDtl = appsmith.model.data;
    rptDtl = JSON.parse(rptDtl);
    let html = ``;
    html += `
		<body>
			<table class="table-grid">
		     <tr class="table-head">
	         <th class="hd-1">Last Name</th>
					 <th class="hd-2">First Name</th>
				   <th class="hd-3">Email</th>
				   <th class="hd-4">Last Login</th>
         </tr>`;
    rptDtl.forEach(acct => {
      html += `
							<tr class="table-row">
									<td class="col-1">${acct.last_name}</td>
									<td class="col-2">${acct.first_name}</td>
									<td class="col-3">${acct.email}</td>
									<td class="col-4">${acct.login}</td>
							</tr>`;
    });
    html += `
					</table>
			</tbody>`;
    return html;
  }
}

//Start rendering the report when widget is ready
appsmith.onReady(() => {
  //rerender the report when the data changes
  appsmith.onModelChange(() => {
    Helper.initApp();
  });
});

