/* Import flaticon Library for the ICONS */
@import url("https://cdn-uicons.flaticon.com/2.3.0/uicons-regular-rounded/css/uicons-regular-rounded.css");
.accordion {
  background-color: var(--appsmith-theme-backgroundColor);
  color: var(--appsmith-theme-secondaryColor);
  cursor: pointer;
  padding: 10px;
  width: 100%;
  border: none;
  text-align: left;
  outline: none;
  font-size: 15px;
  transition: 0.4s;
  font-family: "TimesNewRoman", serif;
}

.accordion i {
  vertical-align: middle; /* Icon vertical position */
  margin-right: 5px; /* Margin with text */
}

.active, .accordion:hover {
  background-color: #ddd;
}

.panel {
  padding: 0 14px;
  display: none;
  background-color: var(--appsmith-theme-backgroundColor);
  overflow: hidden;
}

.sub-option {
  padding: 10px;
  cursor: pointer;
  border-bottom: 1px solid #ccc;
  font-family: "TimesNewRoman", serif;
  font-size: 15px;
  font-color: blue;
  transition: background-color 0.5s ease;
}

.sub-option i {
  vertical-align: text-bottom; /* Icon vertical position */
  margin-right: 5px; /* Margin with text */
}

.sub-option:hover {
  background-color: #f9f9f9; /*#e9e9e9 */
}

.sub-option.active {
  background-color: #e9e9e9;
}

